mapper.select:
select content_id, content_type_id, title, addr1, addr2, tel, first_image, readcount, sido_code, gugun_code, latitude, longitude
from attraction_info
where content_id=#{contentId}

select content_id, content_type_id, title, addr1, addr2, tel, first_image, readcount, sido_code, gugun_code, latitude, longitude from attraction_info
sido_code=#{sidoCode}
and
content_type_id=#{contentTypeId}
and
keyword LIKE CONCAT('%',#{keyword},'%')
