mapper.select:
select id, user_id, user_name, token
from user
where user_id=#{userId} and user_password=#{userPwd}

select user_name, user_id, user_phone
from user
where user_id = #{userId}

mapper.update:
update user
set token = #{token}
where user_id = #{userId}

mapper.select:
select token
from user
where user_id = #{userId}

mapper.update:
update user
set token = #{token, jdbcType=VARCHAR}
where user_id = #{userId}
